sources.sass\\components\\_backdrop.scss:
.backdrop {
  position: fixed;
  top: 0;
  left: 0;
  width: 100vw;
  height: 100vh;
  background: var(--color-bg-backdrop);
  transition: opacity 250ms cubic-bezier(0.4, 0, 0.2, 1),
    visibility 250ms cubic-bezier(0.4, 0, 0.2, 1);
}
.is-hidden {
  opacity: 0;
  visibility: hidden;
  pointer-events: none;
}

.no-scroll {
  overflow-y: hidden;
}

sources.sass\\components\\_border-template.scss:
.thumb {
  width: 133px;
  height: 158px;

  // обираємо форму за закругленням (top left bottom right)

  // tl-br
  border-radius: 50px 0px;
  // tr-bl
  border-radius: 0px 50px;
  // thumb-tr-tl
  border-radius: 72px 72px 0 0;
  // bl
  border-radius: 0px 0px 0px 50px;
  // tr
  border-radius: 0px 50px 0px 0px;

  background-color: #ffffff;

  // якщо потрібна внутрішня тінь
  box-shadow: inset 1px 1px 16px rgba(203, 203, 203, 0.8),
    inset -1px -1px 16px #ffffff;
  // якщо потрібна зовнішня тінь
  box-shadow: 20px 20px 80px rgba(0, 0, 0, 0.3);
}

sources.sass\\components\\_button.scss:
.button-style {
  font-weight: 700;
  font-size: 16px;
  line-height: 1.5;
  letter-spacing: 0.1em;
  cursor: pointer;
  color: var(--color-accent);
  box-shadow: 10px 10px 20px rgba(72, 121, 150, 0.1), 5px 5px 10px rgba(72, 121, 150, 0.25);
  background-color: #f5f5fa;
  border-radius: 30.5px;
  border-color: transparent;

  transition: background-color 250ms cubic-bezier(0.4, 0, 0.2, 1),
    box-shadow 250ms cubic-bezier(0.4, 0, 0.2, 1);

  &:hover,
  &:focus {
    background-color: #f9f9fc;
    box-shadow: 10px 10px 20px rgba(72, 121, 150, 0.05), 5px 5px 10px rgba(72, 121, 150, 0.15);
  }
}

sources.sass\\layout\\_3d_modeling.scss:
// СТИЛИ СЕКЦИЙ 3D МОДДЕЛИНГ
.modelling__section {
  padding-top: 32px;

  @media screen and (min-width: 768px) {
    padding-top: 44px;
  }
  @media screen and (min-width: 1444px) {
    padding-top: 85px;
  }
}
.creating-ways {
  @media screen and (min-width: 280px) {
    padding-top: 52px;
    padding-bottom: 52px;
  }
  @media screen and (min-width: 768px) {
    padding-top: 50px;
  }
  @media screen and (min-width: 1444px) {
    padding-top: 39px;
    padding-bottom: 100px;
  }
}
.creating-ways--nopadding {
  padding-top: 0;
}
.creating-ways--background {
  background-repeat: no-repeat;
  @media screen and (max-width: 767px) {
    background-image: url(../images/mobile/3d-modelling/mashine.jpg);
    height: 440px;
    background-position: center bottom 0px;
  }
  @media (min-device-pixel-ratio: 2) and (min-width: 280px) and (max-width: 767px),
    (min-resolution: 192dpi) and (min-width: 280px) and (max-width: 767px),
    (min-resolution: 2dppx) and (min-width: 280px) and (max-width: 767px) {
    background-image: url(../images/mobile/3d-modelling/mashine@2x.jpg);
    height: 440px;
    background-position: center bottom 0px;
    background-size: 280px;
  }

  @media screen and (min-width: 768px) {
    background-image: url(../images/tablet/3d-modelling/mashine.jpg);
    background-position: right 0px bottom 32px;
    background-size: 40%;
  }
  @media (min-device-pixel-ratio: 2) and (min-width: 768px),
    (min-resolution: 192dpi) and (min-width: 768px),
    (min-resolution: 2dppx) and (min-width: 768px) {
    background-image: url(../images/tablet/3d-modelling/mashine@2x.jpg);
  }

  @media screen and (min-width: 1444px) {
    background-image: url(../images/desktop/3d-modelling/mashine.jpg);
    background-position: right 0px bottom 0px;
    height: 300px;
    background-size: 45%;
  }
  @media (min-device-pixel-ratio: 2) and (min-width: 1444px),
    (min-resolution: 192dpi) and (min-width: 1444px),
    (min-resolution: 2dppx) and (min-width: 1444px) {
    background-image: url(../images/desktop/3d-modelling/mashine@2x.jpg);
  }
}
// СТИЛИ ЗАГОЛОВКА, ТЕКСТОВ
.modelling-title {
  @media screen and (min-width: 280px) {
    @include title-main($fs: 24px, $lh: 30px);
    margin-bottom: 16px;
  }
  @media screen and (min-width: 768px) {
    @include title-main($fs: 36px, $lh: 45px);
  }
  @media screen and (min-width: 1444px) {
    @include title-main($fs: 48px, $lh: 60px);
    margin-bottom: 24px;
  }
}

.creating-ways__title {
  @media screen and (min-width: 280px) {
    @include title-secondary($fs: 18px, $lh: 23px);
    margin-bottom: 16px;
  }
  @media screen and (min-width: 768px) {
    @include title-secondary($fs: 24px, $lh: 30px);
  }
  @media screen and (min-width: 1444px) {
    @include title-secondary($fs: 36px, $lh: 45px);
    padding-top: 85px;
  }
}

.realistic-models {
  @media screen and (min-width: 280px) and (max-width: 767px) {
    @include title-secondary($fs: 16px, $lh: 20px);
  }
  @media screen and (min-width: 1444px) {
    padding-top: 0;
  }
}

.modelling-title__description {
  @media screen and (min-width: 1444px) {
    display: flex;
  }
}
.modelling-title__text {
  @media screen and (min-width: 280px) {
    margin-bottom: 16px;
    min-width: 280px;

    &:last-child {
      margin-bottom: 0;
    }
  }
  @media screen and (min-width: 768px) {
    max-width: 515px;
  }
  @media screen and (min-width: 1444px) {
    margin-bottom: 0;
    margin-right: 32px;
    &:last-child {
      margin-right: 0;
    }
  }
}

// СЕКЦИЯ СПОСОБИ СТВОРЕННЯ МОДЕЛI

.creating-ways__item {
  margin-bottom: 16px;
  &:last-child {
    margin-bottom: 0;
  }
  &:before {
    content: "";
    display: inline-block;
    vertical-align: middle;
    width: 7px;
    height: 7px;

    margin-right: 10px;
    border-radius: 3px 0px;
    background-color: var(--color-accent);
  }
}
.mobile-parttext {
  @media screen and (max-width: 350px) {
    display: block;
    text-indent: 17px;
  }
}

// СЕКЦИЯ "МЫ СОЗДАЁМ РЕАЛИСТИЧНЫЕ ИЗОБРАЖЕНИЯ"
.realistic-models__pictures {
  display: flex;
  @media screen and (max-width: 465px) {
    flex-wrap: wrap;
    flex-basis: calc((100%-10.77px) / 2);
    margin-right: -10.77px;
    max-width: 300px;
    margin: 0 auto;
  }
  @media screen and (min-width: 466px) and (max-width: 767px) {
    flex-wrap: wrap;
    flex-basis: calc((100%-32.31px) / 4);
    margin-right: -10.77px;
  }
  @media screen and (min-width: 768px) {
    flex-wrap: nowrap;
    flex-basis: calc((100%-36.69px) / 4);
    margin-right: -12.23px;
  }
  @media screen and (min-width: 1444px) {
    flex-basis: calc((100%-60px) / 4);
    margin-right: -20px;
  }
}
.realistic-models__pictures-item {
  @media screen and (max-width: 767px) {
    margin-right: 10.77px;
    max-width: 134.62px;
  }
  @media screen and (min-width: 768px) {
    margin-right: 12.23px;
    width: 153px;

    &:last-child {
      margin-right: 0;
    }
  }
  @media screen and (min-width: 1444px) {
    margin-right: 20px;
    width: 250px;
  }
}
// Стили отзывчивого флекса картинок
.realistic-models__pictures-item {
  @media screen and (max-width: 465px) {
    &:nth-child(2n) {
      margin-right: 0;
    }
    &:nth-child(n + 3) {
      margin-top: 16px;
    }
  }
  @media screen and (min-width: 466px) and (max-width: 767px) {
    &:nth-child(n + 4) {
      margin-top: 16px;
    }
    &:nth-child(n + 3) {
      margin-right: 0;
    }
  }
}

.modelling__button-section {
  @media screen and (max-width: 767px) {
    padding-bottom: 75.85px;
  }
  @media screen and (min-width: 768px) and (max-width: 1443px) {
    padding-bottom: 76px;
  }
  @media screen and (min-width: 1444px) {
    padding-bottom: 100px;
  }
}
.modelling-orderlink {
  text-decoration: none;
  color: var(--color-accent);
}
.modelling-orderbutt__button {
  display: block;
  text-align: center;
  border: none;
  letter-spacing: -0.3px;
  margin: 0 auto;

