/* Расстановка хедера */
.header {
  &-logo {
    z-index: 11;
    margin-right: 22px;

    @media screen and (min-width: 768px) {
      margin-right: 176px;
    }

    @media screen and (min-width: 1444px) {
      margin-right: 213px;
    }
  }

  &-menu {
    display: flex;
    justify-content: space-between;
    align-items: center;

    @media screen and (min-width: 768px) {
      justify-content: flex-start;
    }
  }

  &-button {
    border: none;
    background-color: transparent;
    margin-left: 40px;
    cursor: pointer;
    margin-right: 29px;
    padding: 0;
    width: 24px;
    height: 24px;
  }
}

.button-menu {
  display: flex;
  align-items: center;
  justify-content: space-between;
}

.icon-burger {
  fill: #487996;
}

.icon-tell {
  fill: #008cef;

  @media screen and (min-width: 768px) {
    margin-right: 4px;
  }
}

.tablet-tell {
  z-index: 11;

  @media screen and (max-width: 767px) {
    display: none;
  }
  @media screen and (min-width: 1444px) {
    margin-top: 25px;
  }
}

.desctop-nav {
  display: flex;
  align-items: baseline;
  margin-right: 109px;
  z-index: 12;

  @media screen and (max-width: 1443px) {
    display: none;
  }

  &-item {
    font-family: 'Caviar Dreams', sans-serif;
    font-weight: 400;
    font-size: 18px;
    letter-spacing: 0.01em;
  }

  &-item:not(:last-child) {
    margin-right: 54px;
  }
}

// Анимация выпадающего окна

.icon-list {
  vertical-align: middle;
  margin-left: 4px;
  transition: transform 1s ease;
}

.rotate {
  transform: rotate(180deg);
}

.dropbtn {
  border: none;
  background-color: transparent;
  cursor: pointer;
  padding: 0;
  padding-top: 4px;
  padding-bottom: 4px;
}

.dropdown {
  position: relative;
  display: inline-block;
  z-index: 1;

  &-content {
    opacity: 0;
    position: absolute;
    top: 0;
    left: -40px;
    background-color: #ffffff;
    width: 160px;
    height: 111px;
    box-shadow: 20px 20px 80px rgba(0, 0, 0, 0.1);
    z-index: -1;
    padding-top: 33px;
    border-radius: 0 0 50% 50%;
    margin-bottom: -6px;
    transform: translateY(-100%);

    & a {
      text-decoration: none;
      display: block;
      font-weight: 400;
      font-size: 16px;
      line-height: 1.19;
      letter-spacing: 0.01em;
      color: var(--color-main);
      text-align: center;
      margin-bottom: 6px;
    }
  }
}

.show {
  opacity: 1;
  transform: translateY(0%);
  transition: opacity 250ms cubic-bezier(0.4, 0, 0.2, 1);
}

/* Анимация бургера */

.toggle-hamburger {
  background-color: transparent;
  color: #487996;
  display: block;
  position: relative;
  overflow: hidden;
  margin: 0;
  padding: 0;
  width: 32px;
  height: 26px;
  font-size: 0;
  text-indent: -9999px;
  -webkit-appearance: none;
  -moz-appearance: none;
  appearance: none;
  box-shadow: none;
  border: none;
  border-radius: none;
  cursor: pointer;
  -webkit-transition: background 0.3s;
  transition: background 0.3s;
  z-index: 11;

  @media screen and (min-width: 768px) {
    margin-right: 69px;
  }

  @media screen and (min-width: 1444px) {
    display: none;
  }

  &:focus {
    outline: none;
  }

  & span {
    display: block;
    position: absolute;
    top: 12px;
    left: 0px;
    right: 18px;
    height: 2px;
    width: 32px;
    background: #487996;
    border-radius: 2px;

    &::before,
    &::after {
      position: absolute;
      display: block;
      left: 0;
      width: 32px;
      height: 2px;
      background-color: #487996;
      content: '';
    }

    &::before {
      top: -8px;
      border-radius: 2px;
    }
    &::after {
      bottom: -8px;
      border-radius: 2px;
    }
  }

  &.toggle-hamburger__animx span {
    -webkit-transition: background 0s 0.3s;
    transition: background 0s 0.3s;

    &::before,
    &::after {
      -webkit-transition-duration: 0.3s, 0.3s;
      transition-duration: 0.3s, 0.3s;
      -webkit-transition-delay: 0.3s, 0s;
      transition-delay: 0.3s, 0s;
    }
    &::before {
      -webkit-transition-property: top, transform;
      transition-property: top, transform;
    }
    &::after {
      -webkit-transition-property: bottom, transform;
      transition-property: bottom, transform;
    }
  }

  &.toggle-hamburger__animx.is-active span {
    background: none;

    &::before,
    &::after {
      background-color: #487996;
      -webkit-transition-delay: 0s, 0.3s;
      transition-delay: 0s, 0.3s;
    }
    &::before {
      top: 0;
      -webkit-transform: rotate(45deg);
      transform: rotate(45deg);
    }
    &::after {
      bottom: 0;
      -webkit-transform: rotate(-45deg);
      transform: rotate(-45deg);
    }
  }
}

/* Выбор языка */

.custom-select {
  font-family: var(--font-secondary);
  position: relative;
  z-index: 11;
  @media screen and (min-width: 768px) {
    margin-right: 30px;
  }

  @media screen and (min-width: 1444px) {
    margin-right: 89px;
  }

  & select {
    display: none;
  }
}

.select-items {
  width: 50px;
  text-align: center;
  position: absolute;
  top: 100%;
  left: -11px;
  right: 0;
  z-index: 99;
  background-color: #ffffff;
  border-radius: 5px;
  padding-bottom: 20px;
  box-shadow: 0 0 10px rgba(0, 0, 0, 0.5);

  & div {
    padding-top: 20px;
  }
}

.select-items div,
.select-selected {
  font-weight: 700;
  font-size: 14px;
  line-height: 1.28;
  letter-spacing: 0.01em;
  color: #bdbdbd;
  cursor: pointer;
}

.select-hide {
  display: none;
}

/* Номера телефонов */

.window-tell {
  position: relative;
  z-index: 11;

  @media screen and (min-width: 768px) {
    display: none;
  }
}

.tell-menu {
  position: absolute;
  top: 50px;
  left: -25px;
  width: 167px;
  height: 82px;
  z-index: 11;
  background-color: #ffffff;
  box-shadow: 0 0 10px rgba(0, 0, 0, 0.5);
}

.numbers {
  padding: 17px 5px;
}

.tell-number {
  font-family: 'Caviar Dreams', sans-serif;
  font-weight: 400;
  font-size: 15px;
  line-height: 1.25;
  letter-spacing: 0.01em;
  color: #2b2b2b;
}

.tell-item:not(:last-child) {
  margin-bottom: 10px;
}

.triangular-icon {
  position: absolute;
  fill: #ffffff;
  top: -15px;
  left: 38%;
  transform: rotate(-90deg);
}

/* Модалка мобильного меню */

.mobile-menu {
  position: fixed;
  top: 0;
  left: 0;
  background-color: #ffffff;
  width: 100vw;
  height: 100vh;
  z-index: 10;
  background-image: url(../images/background/modal-bg-mobile.jpg);
  background-position: top;
  background-size: cover;
  background-repeat: no-repeat;
  margin: 0 auto;

  @media (min-device-pixel-ratio: 2) and (min-width: 280px),
    (min-resolution: 192dpi) and (min-width: 280px),
    (min-resolution: 2dppx) and (min-width: 280px) {
    background-image: url(../images/background/modal-bg-mobile@2x.jpg);
  }

  @media screen and (min-width: 768px) {
    background-image: url(../images/background/modal-bg-tablet.jpg);
  }

  @media (min-device-pixel-ratio: 2) and (min-width: 768px),
    (min-resolution: 192dpi) and (min-width: 768px),
    (min-resolution: 2dppx) and (min-width: 768px) {
    background-image: url(../images/background/modal-bg-tablet@2x.jpg);
  }

  &-container {
    padding-top: 115px;
    transition: all 500ms cubic-bezier(0.4, 0, 0.2, 1);
  }
}

.nav-mobile-list {
  text-align: center;
  margin-bottom: -24px;
}

.nav-mobile-item {
  margin-bottom: 24px;
}

.nav-mobile-link {
  font-weight: 700;
  font-size: 24px;
  line-height: 1.25;
  color: #487996;
  text-transform: uppercase;
}

.nav-mobile-small-link {
  font-weight: 500;
  text-transform: capitalize;
}

.before-element {
  position: relative;

  &::before {
    position: absolute;
    content: '';
    top: 10px;
    left: -18px;
    width: 7px;
    height: 7px;
    border-radius: 3px 0px;
    background-color: #487996;
  }
}

/* ===================================Задать на каждую страницу отдельно */
.current {
  -webkit-text-fill-color: transparent;
  -webkit-text-stroke: 1px #487996;
}
/* ======================================================================== */

