  }
  @media screen and (min-width: 768px) {
    display: flex;
  }
}
.more-photos__img {
  width: 100%;
  height: auto;
  display: block;
}
.more-photos__button {
  padding: 0;
  border: none;
}
.more-photos__img {
  max-height: auto;
}

// slider

.button-close-modal-slider {
  display: flex;
  border: none;
  text-align: center;
  justify-content: center;
  position: absolute;
  z-index: 10;
  background-color: rgba(43, 43, 43, 0);
  fill: white;
  @media screen and (max-width: 767px) {
    top: 6px;
    left: 237px;
  }
  @media screen and (min-width: 768px) {
    top: 13px;
    left: 572px;
  }
  @media screen and (min-width: 1444px) {
    top: 21px;
    left: 1000px;
  }
}
.size-photo {
  width: 100%;
  height: auto;
  display: block;
}
.button-close-slider {
  @media screen and (max-width: 767px) {
    width: 11px;
    height: 10px;
  }
  @media screen and (min-width: 768px) {
    width: 26px;
    height: 26px;
  }
  @media screen and (min-width: 1444px) {
    width: 42px;
    height: 42px;
  }
}

// modal window

.more-photos-modal-window {
  position: absolute;
  height: auto;
  background-color: transparent;
  border: transparent;
  left: 50%;
  top: 50%;
  transform: translate(-50%, -50%);
  @media screen and (max-width: 767px) {
    max-width: 236px;
  }
  @media screen and (min-width: 768px) {
    max-width: 554px;
  }
  @media screen and (min-width: 1444px) {
    max-width: 906px;
  }
}
.slick-prev {
  position: absolute;

  width: 150px;
  height: 1000px;
  opacity: 0;
  @media screen and (max-width: 767px) {
    top: -100px;
    left: -150px;
  }
  @media screen and (min-width: 768px) {
    top: -100px;
    left: -150px;
  }
  @media screen and (min-width: 1444px) {
    top: -100;
    left: -150px;
  }
}
.slick-next {
  position: absolute;
  width: 150px;
  height: 1000px;
  opacity: 0;
  @media screen and (max-width: 767px) {
    top: -100px;
    left: 237px;
  }
  @media screen and (min-width: 768px) {
    top: -100px;
    left: 555px;
  }
  @media screen and (min-width: 1444px) {
    top: -100px;
    left: 907px;
  }
}
.button-prev-svg {
  position: absolute;
  fill: white;
  background-color: transparent;
  @media screen and (max-width: 767px) {
    width: 11px;
    height: 10px;
    top: 100px;
    left: -10px;
  }
  @media screen and (min-width: 768px) {
    width: 26px;
    height: 26px;
    top: 185px;
    left: -50px;
  }
  @media screen and (min-width: 1444px) {
    width: 42px;
    height: 42px;
    top: 300px;
    left: -120px;
  }
}
.button-next-svg {
  position: absolute;
  fill: white;
  background-color: transparent;
  @media screen and (max-width: 767px) {
    width: 11px;
    height: 10px;
    top: 100px;
    left: 237px;
  }
  @media screen and (min-width: 768px) {
    width: 26px;
    height: 26px;
    top: 185px;
    left: 572px;
  }
  @media screen and (min-width: 1444px) {
    width: 42px;
    height: 42px;
    top: 300px;
    left: 1000px;
  }
}
.button-prev {
  padding: 0;
  border: none;
  font: inherit;
  color: inherit;
  background-color: transparent;
}
.button-next {
  padding: 0;
  border: none;
  font: inherit;
  color: inherit;
  background-color: transparent;
}

