.select__icon {
  display: flex;
  align-items: center;
  justify-content: center;
  flex-shrink: 0;

  margin-left: auto;

  width: 0;
  height: 0;
  border-style: solid;
  border-width: 13px 9.5px 0 9.5px;
  border-color: #000000 transparent transparent transparent;
}
.select__body {
  display: none;
  border-bottom: 1px solid #487996;
  border-top: 0;
  position: absolute;
  left: 0;
  right: 0;
  top: 100%;

  background-color: white;

  z-index: 99;
}

.select__item {
  cursor: pointer;

  padding: 8px;
}
.select__item:hover {
  background-color: rgba(72, 121, 150, 0.5);
  color: white;
}
.select__name {
  display: flex;
  margin-right: auto;

  padding: 8px;
  padding-left: 0;

  @include txt($fs: 16px, $lh: 20px);
}

.form__wrapper {
  margin-bottom: 44px;
}

input[type='file'] {
  display: none;
}

