.we-create {
  margin: 0 auto;
  &__title {
    @include title-main($fs: 24px, $lh: 30px);
    margin-bottom: 16px;
  }

  &__list-products {
    display: flex;
    flex-wrap: wrap;
  }
  &__item-products {
    display: flex;
    position: relative;
    justify-content: center;
  }
  &__item-products:not(:nth-last-child(-n + 3)) {
    margin-bottom: 15px;
  }

  &__thub-tl-br_products {
    display: flex;
    position: relative;
    justify-content: center;
    text-align: center;

    width: 132px;
    height: 158px;
    border-radius: 50px 0px;
    background-color: #ffffff;
    box-shadow: inset 1px 1px 16px rgba(203, 203, 203, 0.8),
      inset -1px -1px 16px #ffffff;
  }

  &__img_products {
    position: absolute;
  }
  &__creation {
    top: 30px;
  }
  &__template {
    top: 38px;
  }
  &__decor {
    top: 32px;
  }
  &__3d-coub {
    top: 23px;
  }

  &__subtitle-products {
    @include txt(
      $fs: 14px,
      $fw: 400,
      $lh: 17px,
      $t-align: center,
      $color: var(--color-accent)
    );
    text-align: center;
    position: absolute;
    top: 118px;
    text-transform: uppercase;
  }

  &__item-offer:not(:last-child) {
    margin-bottom: 52px;
  }
  &__img-offer {
    border-radius: 50px 0px;
    border-radius: 50px 0px;
    box-shadow: 20px 20px 80px rgba(0, 0, 0, 0.15);
  }

  &__txt {
    @include txt($fs: 16px, $lh: 20px);
  }
  &__txt:not(:nth-last-child(-n + 2)) {
    margin-bottom: 16px;
  }
  &__txt_2p:not(:nth-last-child(-n + 3)) {
    margin-bottom: 16px;
  }
  &__txt-span {
    @include txt($fs: 16px, $fw: 700, $lh: 20px, $color: var(--color-accent));
    text-transform: uppercase;
  }
  &__button {
    padding: 16px 32px;
    letter-spacing: -0.2px;
    font-weight: 700;
    font-size: 16px;
    line-height: 1.5;
  }
}

@media screen and (max-width: 767px) {
  .we-create {
    &__title {
      margin-bottom: 24px;
    }
    &__list-products {
      margin-bottom: 52px;
    }
    &__item-products {
      flex-basis: calc(100% / 2 - 15px);
    }
    &__item-products:nth-child(2n-1) {
      margin-right: 15px;
    }

    &__img-offer {
      margin-bottom: 16px;
    }
    &__button {
      margin-top: 24px;
    }
  }
}
@media screen and (min-width: 480px) and (max-width: 767px) {
  .we-create {
    &__item-products:not(:nth-last-child(-n + 3)) {
      margin-bottom: 60px;
    }
    &__subtitle-products {
      max-width: 200px;
    }
  }
}

@media screen and (min-width: 480px) {
  .we-create {
    &__thub-tl-br_products {
      width: 152px;
      height: 182px;
    }
    &__creation {
      top: 30px;
    }
    &__template {
      top: 43px;
    }
    &__decor {
      top: 36px;
    }
    &__3d-coub {
      top: 27px;
    }
    &__subtitle-products {
      top: 136px;
    }
  }
}

@media screen and (min-width: 768px) {
  .we-create {
    &__list-products {
      margin-bottom: 52px;
    }
    &__item-products:not(:last-child) {
      margin-right: 13px;
    }
    &__title {
      @include title-main($fs: 36px, $lh: 45px);
      margin-bottom: 24px;
    }

    &__item-offer {
      display: flex;
    }
    &__item-offer:not(:last-child) {
      margin-bottom: 76px;
    }
    &__border {
      margin-right: 20px;
    }
    &__description {
      width: 381px;
    }
    &__button {
      margin-top: 52px;
    }
  }
}

@media screen and (min-width: 1444px) {
  .we-create {
    &__title {
      @include title-main($fs: 48px, $lh: 60px);
      margin-bottom: 24px;
    }
    &__thub-tl-br_products {
      width: 250px;
      height: 298px;
    }
    &__item-products:not(:last-child) {
      margin-right: 20px;
    }
    &__creation {
      top: 56px;
    }
    &__template {
      top: 77px;
    }
    &__decor {
      top: 64px;
    }
    &__3d-coub {
      top: 50px;
    }
    &__subtitle-products {
      top: 235px;
    }
    &__border {
      margin-right: 78px;
    }
    &__item-offer:not(:last-child) {
      margin-bottom: 100px;
    }
    &__description {
      width: 734px;
    }

    &__txt {
      @include txt($fs: 18px, $lh: 23px);
    }
    &__txt:not(:nth-last-child(-n + 2)) {
      margin-bottom: 24px;
    }
    &__txt_2p:not(:nth-last-child(-n + 3)) {
      margin-bottom: 24px;
    }
    &__txt-span {
      @include txt($fs: 18px, $fw: 700, $lh: 23px, $color: var(--color-accent));
      text-transform: uppercase;
    }
  }
}
